/** *    author:  _Berlin_ *    created: 14.11.2025 10:59:06 AM**/#include <bits/stdc++.h> using namespace std; #ifdef BERLIN#include "algo/debug.h"#else#define debug(...) 42#endif int32_t main() {  ios_base::sync_with_stdio(false);  cin.tie(nullptr);   int a, b;  cin >> a >> b;   vector<int> transf;   function<void(int)> dfs = [&] (int u) {    transf.push_back(u);     if(u == a) return;    if(u < a) { cout << "NO\n"; exit(0);}     if(!(u & 1)) u >>= 1;    else if(u % 10 == 1) u /= 10;    else { cout << "NO\n"; exit(0);}    dfs(u);  };   dfs(b);   if(transf.back() != a) { cout << "NO\n"; exit(0);}   reverse(transf.begin(), transf.end());   cout << "YES\n";  cout << (int)transf.size() << "\n";  for(auto &i : transf) cout << i << " ";  cout << "\n";    return 0;}